import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";



const Dashboard = () => {
  const [bookmarks, setBookmarks] = useState([]);
  const [formData, setFormData] = useState({ title: "", url: "", category: "" });
  const [editingBookmark, setEditingBookmark] = useState(null);
  const navigate = useNavigate();
  const token = localStorage.getItem("token");

  useEffect(() => {
    if (!token) {
      navigate("/login");
    } else {
      fetchBookmarks();
    }
  }, [token, navigate]);

  // Fetch bookmarks
  const fetchBookmarks = async () => {
    try {
      const res = await axios.get("http://localhost:5000/api/bookmarks", {
        headers: { "x-auth-token": token },
      });
      setBookmarks(res.data);
    } catch (err) {
      console.error("Error fetching bookmarks:", err);
    }
  };

// Toggle favorite status
const toggleFavorite = async (id) => {
  try {
    await axios.put(`http://localhost:5000/api/bookmarks/favorite/${id}`, {}, {
      headers: { "x-auth-token": token },
    });
    fetchBookmarks(); // Refresh list after updating
  } catch (err) {
    console.error("Error updating favorite:", err);
  }
};

  // Handle input change
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Add new bookmark
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post("http://localhost:5000/api/bookmarks/add", formData, {
        headers: { "x-auth-token": token },
      });
      fetchBookmarks();
      setFormData({ title: "", url: "", category: "" });
    } catch (err) {
      console.error("Error adding bookmark:", err);
    }
  };

  // Delete bookmark
  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/bookmarks/delete/${id}`, {
        headers: { "x-auth-token": token },
      });
      fetchBookmarks();
    } catch (err) {
      console.error("Error deleting bookmark:", err);
    }
  };

  // Logout
  const handleLogout = () => {
    localStorage.removeItem("token");
    navigate("/login");
  };

  const handleEdit = (bookmark) => {
    setEditingBookmark(bookmark);
    setFormData({ title: bookmark.title, url: bookmark.url, category: bookmark.category });
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    if (!editingBookmark) return;
  
    try {
      await axios.put(`http://localhost:5000/api/bookmarks/update/${editingBookmark._id}`, formData, {
        headers: { "x-auth-token": token },
      });
  
      setEditingBookmark(null);
      setFormData({ title: "", url: "", category: "" });
      fetchBookmarks();
    } catch (err) {
      console.error("Error updating bookmark:", err);
    }
  };
  
  

  return (
    <div className="container mt-5">
      <h2 className="text-center">My Bookmarks</h2>
      <button className="btn btn-danger float-end" onClick={handleLogout}>Logout</button>

      {/* Add Bookmark Form */}
      <form onSubmit={editingBookmark ? handleUpdate : handleSubmit} className="mb-4">
  <div className="mb-3">
    <input type="text" name="title" placeholder="Bookmark Title" className="form-control"
      value={formData.title} onChange={handleChange} required />
  </div>
  <div className="mb-3">
    <input type="url" name="url" placeholder="Bookmark URL" className="form-control"
      value={formData.url} onChange={handleChange} required />
  </div>
  <div className="mb-3">
    <input type="text" name="category" placeholder="Category" className="form-control"
      value={formData.category} onChange={handleChange} required />
  </div>
  <button type="submit" className="btn btn-primary">
    {editingBookmark ? "Update Bookmark" : "Add Bookmark"}
  </button>
  {editingBookmark && (
    <button type="button" className="btn btn-secondary ms-2" onClick={() => setEditingBookmark(null)}>
      Cancel
    </button>
  )}
</form>


      {/* Display Bookmarks */}
      {bookmarks.length === 0 ? (
        <p>No bookmarks added yet.</p>
      ) : (
        <table className="table table-striped">
          <thead>
            <tr>
              <th>Title</th>
              <th>URL</th>
              <th>Category</th>
              <th>Favorite</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {bookmarks.map((bookmark) => (
              <tr key={bookmark._id}>
                <td>{bookmark.title}</td>
                <td><a href={bookmark.url} target="_blank" rel="noopener noreferrer">{bookmark.url}</a></td>
                <td>{bookmark.category}</td>
                <td>
                  <button
                    className={`btn btn-${bookmark.favourite ? "success" : "outline-secondary"} btn-sm`}
                    onClick={() => toggleFavorite(bookmark._id)}
                  >
                    {bookmark.favorite ? "⭐" : "☆"}
                  </button>
                </td>
                <td>
  <button className="btn btn-warning btn-sm" onClick={() => handleEdit(bookmark)}>Edit</button>
  <button className="btn btn-danger btn-sm ms-2" onClick={() => handleDelete(bookmark._id)}>Delete</button>
</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Dashboard;
